// Seems this function is return total amount of avail space instead of space used. 
// Need to re-work to show acurate size of install. 
function dataSize() { 
$Bytes = disk_total_space(ABSPATH); 
$Type=array("", "kilo", "mega", "giga","tera"); 
$counter=0; 
while($Bytes>=1024) { 
$Bytes/=1024; 
$counter++;
}
return("".$Bytes." ".$Type[$counter]."bytes");
}

// Function will return a query of database using get_option and a foreach loop,
// to number and list the contentes of the array. 
function activePlugins() {
$activeplugs = get_option('active_plugins');
$total = 0;
foreach ($activeplugs as $key => $value)
{
$total++;
echo $total.' = '.$value.'<br>';
}
echo '<hr>Total Active Plugin(s) = '.$total;
}


// The Query for admin users
function adminUsers() {
$user_query = new WP_User_Query( array( 'role' => 'Administrator' ) );
// User Loop - to pull all active users.
if ( ! empty( $user_query->results ) ) {
	foreach ( $user_query->results as $user ) {
	echo $user->display_name;
		}
	} else {
		echo 'No users found.';
	}
}
// Escaped a few special characters to allow the command to pass through PHP.
// Intended to only be accessible to run on compatible systems. 
function shellInodes(){
	echo ('<pre>'.shell_exec("find * -maxdepth 0 -type d -exec sh -c \"echo -n {} ' ' ; ls -lR {} | wc -l\" \\;").'</pre>');
}

// This function will retreive all pages identified by WordPress 
function pageIds() {
$page_ids=get_all_page_ids();
foreach($page_ids as $page){
	$uri = get_page_uri($page);
	switch ($uri) {
		case '':
		echo get_the_title($page).' - No Link (Page is not public)<br>';
		break;
		default: 
		echo get_the_title($page).'<a href="'. $uri .'"> - Link to page</a><br>';
		break;
	}
	}
}


// Similar to pageIds() this function will use WP defined get_posts function to request specifics
// In this case we are looking for any status and returning the post IDs. 
function postIds() {
$post_ids = get_posts(array(
        'posts_per_page'=> -1,
		'post_status' => 'any',
        'fields'        => 'ids', // Only get post IDs
    ));
	// foreach iteration will take all values of the given array and act on each value.
foreach($post_ids as $post){
	// $puri is a holder for the URI for the specific Post ID as set in each iteration. 
	$puri = get_page_uri($post);
	switch ($puri) {
		//If we get an empty string on the URI then we report as such.
		case '':
		echo get_the_title($post).' - No Link (Page is not public)<br>';
		break;
		default: 
		echo get_the_title($post).'<a href="'. $puri .'"> - Link to page</a><br>';
		break;
	}	
}
}

function systemsCheck(){
	if(PHP_SHLIB_SUFFIX == 'dll'){
			return '<h3>Some features of WP-PBF do not function on Windows Hosting</h3>';
		}
		// Check if shell_exec is disabled
		if(!is_callable('shell_exec')||(strpos(ini_get('disable_functions'), 'shell_exec') === true)){
			return '<h3>PHP ability to use shell_exec has been disabled. Fatures are missing.</h3>';
		}
		
		// Check if PDO is installed / enabled
		if(!class_exists('PDO')){
			return '<h1>PDO is not installed!</h1>';
		}
	
}




